// import logo from './logo.svg';
// import './App.css';
import TradingViewWidget, { Themes } from 'react-tradingview-widget';
import Top from './component/Top';
import Tokens from './component/Tokens';
import Chart from './component/Chart';
import Orderbook from './component/Orderbook';
import Orderhistory from './component/Orderhistory';
import Trading from './component/Trading';
import Tradehistory from "./component/Transaction"
import useSharedState from './hook/useSharedState'
import { Web3ReactProvider } from '@web3-react/core'
import { Web3Provider } from '@ethersproject/providers'

function getLibrary(provider) {
  const library = new Web3Provider(provider)
  library.pollingInterval = 12000
  return library
}

function App() {
  const {selectedToken,setSelectedToken}=useSharedState();//useState(undefined);
  return (
  <Web3ReactProvider getLibrary={getLibrary}>
        <Top></Top>
        <div className="container-fluid">
          <div className="row">
            <div className="order-2 order-lg-1 col-12 col-lg-2 p-1 bg-dark text-white filters-widget d-none d-xl-block">
              <Tokens></Tokens>
                <Tradehistory></Tradehistory>
            </div>
            <Chart></Chart>
            {(!selectedToken || selectedToken.info.listed) &&
              <Orderbook></Orderbook>
            }
          </div>
          <div className="row">
            <Orderhistory></Orderhistory>
            <Trading></Trading>
          </div>
        </div>
      </Web3ReactProvider>
  );
}

export default App;
